function transform (message, context) {
  // Put your code here.

  // Remove current version from array
  const newArray = message.payload.__versions.reduce((acc, version) => {
    if (version.xstmtvrsVersId !== message.payload.payload.workingTemplateVersion.xstmtvrsVersId) {
      acc.push(version);
    }
    return acc
  }, [])

  // Check other versions for any active version
  const hasActiveVersion = newArray.some(p => p.xstmtvrsIsActive === "true");
  if (hasActiveVersion) {
    throw new Error ("Active version already!")
  }
  const noActiveVersion = !hasActiveVersion;

  message.payload.__noActiveVersion = noActiveVersion;
  return message;
}

function transform (message, context) {

    const p = message.payload.payload.workingTemplateVersion;

