function transform (message, context) {

  const p = message.payload.payload;

  if (p.workingTemplateVersion.xstmtvrsStatusEnum != "published") {
    throw new Error("Only published templates may be archived.")
  }

  if (
    !p.workingTemplateVersion.id ||
    !p.workingTemplateVersion.xstmtvrsTemplateId ||
    !p.workingTemplateVersion.xstmtvrsVersId
  ) {
    throw new Error("An unexpected error occurred.")
  }

  context.set('xstmtvrsTemplateId', p.workingTemplateVersion.xstmtvrsTemplateId)
  context.set('versionId', p.workingTemplateVersion.id)
  context.set('xstmtvrsVersId', p.workingTemplateVersion.xstmtvrsVersId)

  message.payload.__context = context;
  return message;
}

function transform (message, context) {
  // Put your code here.

  const version = message.payload.__version;
  const versions = message.payload.__templateVersions;
  if (version.xstmtvrsStatusEnum !== "published") {
    throw new Error ("Version is not published!")
  }
  if (version.xstmtvrsIsActive !== "false") {
    throw new Error ("Version is active! Cannot be archived.")
  }
  const publishedCount = versions.filter(v => v.xstmtvrsStatusEnum === "published").length;
  if (publishedCount <= 1) {
    throw new Error('Cannot archive only published template!');
  }

  context.set('validToArchive', true)

  return message;
}

function transform (message, context) {
  // Put your code here.
  const date = Date.now();
  context.set('date', date);
  const userId = context.__user?.id;

  let body;

  const version = message.payload.__version

  body = {
    ...version,
    xstmtvrsStatusEnum: "archived",
    xstmtvrsArchivedAt: context.get('date'),
    xstmtvrsArchivedBy: userId
  }

  message.payload.body = body;

  return message;
}
