function transform (message, context) {

  const p = message.payload.payload;

  if (p.selectedTemplateVersion.xstmtvrsStatusEnum != "draft") {
    throw new Error("Only draft templates may be deleted.")
  }

  if (
    !p.selectedTemplate.xstmtmplTemplateId ||
    !p.selectedTemplate.id ||
    !p.selectedTemplateVersion.xstmtvrsVersId ||
    !p.selectedTemplateVersion.id
  ) {
    throw new Error("An unexpected error occurred.")
  }
  // Put your code here.
  context.set('xstmtmplTemplateId', p.selectedTemplate.xstmtmplTemplateId)
  context.set('templateId', p.selectedTemplate.id)

  context.set('xstmtvrsVersId', p.selectedTemplateVersion.xstmtvrsVersId)
  context.set('templateVersionId', p.selectedTemplateVersion.id )

  message.payload.__context = context;
  return message;
}

function transform (message, context) {
  // Put your code here.
  const noPublishedVersion = !message.payload.__templateVersions.some(v => v.xstmtvrsStatusEnum === 'published')

  context.set('noPublishedVersion', noPublishedVersion)

  message.payload.__context = context;

  return message;
}
