function transform (message, context) {
  // Put your code here.
  const templateVersion = message.payload.__templateVersion[0];
  
  let concatenatedContent = '';
  
  for (let i = 1; i <= 8; i++) {
    const fieldName = `xstmtvrsContent${i}`;
    const content = templateVersion[fieldName];
    
    if (content) {
      concatenatedContent += content;
    }
  }
  
  message.payload.__html = concatenatedContent;
  
  return message;
}

function transform (message, context) {
  // Put your code here.

  const html = message.payload.__html;
  const id = context.__user.id;
  context.set('html', html);
  context.set('id', id);

  // Match all data-variable="..." in <variable> tags
  const regex = /<variable[^>]*data-variable="([^"]+)"[^>]*>/g;
  const variables = [];
  let match;

  // Extract all variable names
  while ((match = regex.exec(html)) !== null) {
    const varName = match[1];
    variables.push({ name: varName, value: "" });
  }

  const apiCallPlan = {};
  variables.forEach(obj => {
    const key = obj.name;
    apiCallPlan[key] = true;
  });

  message.payload.__variables = variables;

  // Store in context for later use
  context.set('variables', variables)
  context.set('apiCallPlan', apiCallPlan)

  return message;
}

