function transform (message, context) {
  // Put your code here.

  const person = message.payload.__person
  const name = person.names.find(n => n.preference === 'preferred');

  const enrollmentStatus = message.payload.__studentAcademicPeriods?.[0]?.academicStatuses?.[0]?.academicPeriodStatus?.title || "";
  const generalStudentCurricula = message.payload.__generalStudentCurricula || [];
  const studentAcademicPrograms = message.payload.__studentAcademicPrograms || [];
  const aSIA = message.payload.__additionalStudentInformationAttribute || [];

  const attr = aSIA?.[0]?.SGRSATT?.find(a => a.attsCode === "MFT" || a.attsCode === "MPT");

  const ftPtStatus = attr
    ? (attr.attsCode === "MFT" ? "Full Time" : "Part Time")
    : null;

const trimesterEntryCode = generalStudentCurricula.find(o => o.stypCode)?.stypCode;
const trimesterEntryDesc = generalStudentCurricula.find(o => o.stypCodeDesc)?.stypCodeDesc;



  // Put variable paths etc here
  const masterVariableMap = {
    "DATE_OF_BIRTH": person.dateOfBirth,
    "BANNER_ID": person.credentials.find(c => c.type === 'bannerId')?.value,
    "TITLE": name?.title,
    "FIRST_NAME": name?.firstName,
    "MIDDLE_NAME": name?.middleName,
    "LAST_NAME": name?.lastName,
    "ENROLLMENT_STATUS": enrollmentStatus,
    "ADMIT_TERM_CODE": generalStudentCurricula?.[0]?.admtTermDesc,
    "CAMPUS_CODE": generalStudentCurricula?.[0]?.campCode,
    "CAMPUS_DESC": generalStudentCurricula?.[0]?.campDesc,
    "START_DATE": studentAcademicPrograms?.[0]?.startOn,
    "END_DATE": studentAcademicPrograms?.[0]?.endOn,
    "PROGRAM_CODE": generalStudentCurricula?.[0]?.program,
    "PROGRAM_DESC": generalStudentCurricula?.[0]?.programDesc,
    "MAJOR_CODE": generalStudentCurricula?.[0]?.degcCode,
    "MAJOR_DESC": generalStudentCurricula?.[0]?.degcDesc,
    "LEVEL_CODE": generalStudentCurricula?.[0]?.levlCode,
    "LEVEL_DESC": generalStudentCurricula?.[0]?.levlDesc,
    "TRIMESTER_ENTRY_CODE": trimesterEntryCode,
    "TRIMESTER_ENTRY_DESC": trimesterEntryDesc,
    "FT_PT_STATUS": ftPtStatus

  }

  const variables = message.payload.__variables;
  const data = message.payload.__persons?.[0];

  variables.forEach(v => {
    const key = v.name;
    v.value = masterVariableMap[key] || "";
  });

  message.payload.__variables = variables;

  context.set('variables', variables)
  return message;
}

function transform (message, context) {
  // Put your code here.
  let html = context.get('html'); 

  const variables = message.payload.__variables;

  variables.forEach(v => {
    const pattern = new RegExp(`<variable[^>]*data-variable="${v.name}"[^>]*>.*?</variable>`, 'g');
    html = html.replace(pattern, v.value);
  });

  message.payload = html;


  return message;
}

