function transform (message, context) {
  // Put your code here.
  const p = message.payload.payload;
  const date = Date.now();
  context.set('date', date)
  const userId = context.__user?.id;

  const xstmtmplId = p.editableTemplate.id;
  const xstmtvrsId = p.workingTemplateVersion.id;

  if (!xstmtmplId || !userId || !xstmtvrsId) {
    throw new Error("Something went wrong. Please try again.");
  };

  // Set published
  message.payload.payload.workingTemplateVersion.xstmtvrsStatusEnum = 'published';
  message.payload.payload.workingTemplateVersion.xstmtvrsPublishedAt = context.get('date');
  message.payload.payload.workingTemplateVersion.xstmtvrsPublishedBy = userId;

  context.xstmtmplId = xstmtmplId;
  context.xstmtvrsId = xstmtvrsId;
  return message;
}

